  // Write your method(s) below:
  public static String checkPassword(String pw) {
		int numLetters = 0, numDigits = 0;
		String message;

		// IF the length is less than 8, it's not valid!
		if (pw.length() < 8) {
			message = "The entered password is less than 8 characters.";
			return message;
		}
		// ELSE, so okay, the length is fine...let's keep digging
		else {
			// Count the number of digits and letters in the password
			for (int i = 0; i < pw.length(); i++) {
				if (Character.isDigit(pw.charAt(i))) {
					numDigits++;
				}
				if (Character.isLetter(pw.charAt(i))) {
					numLetters++;
				}
			}

			// Remember, the pw can ONLY contain letters and digits
			// IF the letters and digits don't add up to the length, then we have a problem...
			if (numLetters + numDigits != pw.length()) {
				message = "The entered password contains illegal characters.";
				return message;
			}

			// IF the digits are less than 2, we have a problem...
			else if (numDigits < 2) {
				message = "The entered password contains less than two digits.";
				return message;
			}
			else {
				message = "The entered password is a valid password.";
				return message;
			}
		}
	}

  public static void main(String[] args) {
