  // Write your method(s) below:
  public static String convertNumber(String number) {
		// Make number uppercase before continuing
		number = number.toUpperCase();

		String phoneNumber = "";

		// Loop over all characters in the number
		// We will only print if the character is a digit or a letter
		for (int i = 0; i < number.length(); i++) {
			// IF the given character is a digit, just print it directly.
			if (Character.isDigit(number.charAt(i))) {
				phoneNumber += number.charAt(i);
			}
			// IF the given character is a letter, print the appropriate
			// numerical equivalent by invoking the getNumber() method.
			if (Character.isLetter(number.charAt(i))) {
				phoneNumber += getNumber(number.charAt(i));
			}
		}
		return phoneNumber;
	}

	public static int getNumber(char uppercaseLetter) {
		int number = 0;

		switch (uppercaseLetter) {
			case 'A':
			case 'B':
			case 'C':
				number = 2;
				break;
			case 'D':
			case 'E':
			case 'F':
				number = 3;
				break;
			case 'G':
			case 'H':
			case 'I':
				number = 4;
				break;
			case 'J':
			case 'K':
			case 'L':
				number = 5;
				break;
			case 'M':
			case 'N':
			case 'O':
				number = 6;
				break;
			case 'P':
			case 'Q':
			case 'R':
			case 'S':
				number = 7;
				break;
			case 'T':
			case 'U':
			case 'V':
				number = 8;
				break;
			case 'W':
			case 'X':
			case 'Y':
			case 'Z':
				number = 9;
				break;
		}

		return number;
	}

