  // Write your method(s) below
public static int countPrimes(int max) {
		int count = 0;

		for (int i = 2; i <= max; i++) {
			if (isPrime(i)) {
				// Increment the numPrimesFound
				count++;
			}
		}

		return count;
	}

	public static boolean isPrime(int n) {
		// Here, we say, "yes, let's assume the number is prime".
		boolean prime = true;

		for (int i = 2; i <= n / 2; i++) {
			if (n % i == 0) {
				// But now, if we find that the given number, n, is divisible
				// by some other number, i, we immediately set prime to false and break
				prime = false;
				break;
			}
		}

		// Finally, we return the value of prime. If it's true, that means we never
		// encountered a situation where it was divisible by another number.
		// But if it's false, this means it was in fact divisible by some i.
		return prime;
	}

  public static void main(String[] args) {
    System.out.println(countPrimes(10));
    System.out.println(countPrimes(100));
    System.out.println(countPrimes(1000));
  }
