                    }
                    else if (choiceXYRmenu.equalsIgnoreCase("R")) {
                        System.out.println();
                    }
                    else {
                        System.out.println("\n    Invalid selection --- Try again.");
                    }
                } while (!choiceXYRmenu.equalsIgnoreCase("R"));
            }
