","defaultFilePath":"","defaultFileType":"","extraFiles":{"U3RvY2suamF2YQ==":{"contents":"class Stock {
	// Data Members
	private String tickerSymbol;
	private String companyName;
	private int price;
	private int totalShares;
	private long marketCap;
	private double percentChange;

	// Constructors
	public Stock(String tickerSymbol, String companyName, int price, int totalShares) {
		this.tickerSymbol = tickerSymbol;
		this.companyName = companyName;
		this.price = price;
		this.totalShares = totalShares;
		this.percentChange = 0;
		this.marketCap = (long) totalShares * price;
	}

	// Accessor & Mutator Methods not needed for this class
	public String getTickerSymbol() {
		return tickerSymbol;
	}

	public void setTickerSymbol(String tickerSymbol) {
		this.tickerSymbol = tickerSymbol;
	}

	public String getCompanyName() {
		return companyName;
	}

	public void setCompanyName(String companyName) {
		this.companyName = companyName;
	}

	public int getPrice() {
		return price;
	}

	public void setPrice(int price) {
		this.price = price;
	}

	public int getTotalShares() {
		return totalShares;
	}

	public void setTotalShares(int totalShares) {
		this.totalShares = totalShares;
	}

	public long getMarketCap() {
		return marketCap;
	}

	public void setMarketCap(long marketCap) {
		this.marketCap = marketCap;
	}

	public double getPercentChange() {
		return percentChange;
	}

	public void setPercentChange(double percentChange) {
		this.percentChange = percentChange;
	}

	// Other Methods
	public void adjustPrice(int change) {
		int diff = (price + change) - price; //price - change;

		percentChange = (1.0 * diff / price) * 100;
		price += change;

		marketCap = (long) totalShares * price;
	}

	public String returnStockToDisplay() {
		String result = "";
		String plusminus = "";

		if (percentChange > 0) {
			plusminus = "+";
		}

		result += String.format("Ticker Symbol: %s\n", tickerSymbol);
		result += String.format("Company Name:  %s\n", companyName);
		result += String.format("Current Price: $%d (%s%.1f%%)\n", price, plusminus, percentChange);
		result += String.format("Market Cap:    $%d\n", marketCap);

		return result;
	}
}
