	// Data Members:
	private String label;
	private int price;
	private String category;
	private boolean hasExpiration;
	private int stock;

	// Constructors:
	public StoreProduct(String label, int price, String category, boolean hasExpiration, int stock) {
		this.label = label;
		this.price = price;
		this.category = category;
		this.hasExpiration = hasExpiration;
		this.stock = stock;
	}

	public StoreProduct(String label, int price, int stock) {
		this.label = label;
		this.price = price;
		this.stock = stock;
		this.category = "misc";
		this.hasExpiration = false;
	}

	public StoreProduct(String label, int price) {
		this.label = label;
		this.price = price;
		this.stock = 0;
		this.category = "misc";
		this.hasExpiration = false;
	}

	public StoreProduct(String label, int price, String category, boolean hasExpiration) {
		this.label = label;
		this.price = price;
		this.category = category;
		this.hasExpiration = hasExpiration;
		this.stock = 0;
	}

	// Accessor and Mutator Methods:
	public String getLabel() {
		return label;
	}

	public void setLabel(String label) {
		this.label = label;
	}

	public int getPrice() {
		return price;
	}

	public void setPrice(int price) {
		this.price = price;
	}

	public String getCategory() {
		return category;
	}

	public void setCategory(String category) {
		this.category = category;
	}

	public boolean getHasExpiration() {
		return hasExpiration;
	}

	public void setHasExpiration(boolean hasExpiration) {
		this.hasExpiration = hasExpiration;
	}

	public int getStock() {
		return stock;
	}

	public void setStock(int stock) {
		this.stock = stock;
	}

	// OTHER METHODS
	public void expired(boolean hasExpired) {
		if (hasExpiration && hasExpired) {
			this.stock = 0;
		}
	}

	public boolean customerPurchaseItem(int purchaseQuantity) {
		if (purchaseQuantity <= stock) {
			this.stock -= purchaseQuantity;
			return true;
		}
		else {
			return false;
		}
	}

	public double getDiscountedPrice(double discount) {
		return this.price * (1 - discount);
	}


}
