public class Person {

	// Data Members
	private String firstName;
	private String lastName;
	private int birthDay;
	private int birthMonth;
	private int birthYear;
	private String SSN;

	public Person() {
		firstName = "unassigned";
		lastName = "unassigned";
		SSN = "unassigned";
	}

	public Person(String firstName, String lastName, int birthDay, int birthMonth, int birthYear, String SSN) {
		this.firstName = firstName;
		this.lastName = lastName;
		this.birthDay = birthDay;
		this.birthMonth = birthMonth;
		this.birthYear = birthYear;
		this.SSN = SSN;
	}

	public String getSSN() {
		return SSN;
	}

	public void setSSN(String SSN) {
		this.SSN = SSN;
	}

	public String getFirstName() {
		return firstName;
	}

	public void setFirstName(String firstName) {
		this.firstName = firstName;
	}

	public String getLastName() {
		return lastName;
	}

	public void setLastName(String lastName) {
		this.lastName = lastName;
	}

	public int getBirthMonth() {
		return birthMonth;
	}

	public void setBirthMonth(int birthMonth) {
		this.birthMonth = birthMonth;
	}

	public int getBirthDay() {
		return birthDay;
	}

	public void setBirthDay(int birthDay) {
		this.birthDay = birthDay;
	}

	public int getBirthYear() {
		return birthYear;
	}

	public void setBirthYear(int birthYear) {
		this.birthYear = birthYear;
	}

	// Other Methods
	public String getBirthDate() {
		if (birthDay == 0 || birthMonth == 0 || birthYear == 0) {
			return "unassigned";
		}
		else {
			return String.format("%d/%d/%d", birthDay, birthMonth, birthYear);
		}
	}

	public String isValidSSN() {
		if (SSN.length() != 11) {
			return "invalid";
		}
		for (int i = 0; i < SSN.length(); i++) {
			if (i == 3 || i == 6) {
				// IF the character at index 3 or index 6 is NOT a '-', return false
				if (SSN.charAt(i) != '-') {
					return "invalid";
				}
			}
			else {
				// IF the character at index 'i' is NOT a digit, return false
				if (Character.isDigit(SSN.charAt(i)) == false) {
					return "invalid";
				}
			}
		}

		// IF we've made it till here, then the SSN is valid...thus, return true
		return "valid";
	}

	@Override
	public String toString() {
		String s = "";

		s += "Personal Information:\n";
		s += String.format("First Name: %s\n", firstName);
		s += String.format("Last Name: %s\n", lastName);
		s += String.format("Birth Date: %s\n", getBirthDate());
		s += String.format("SSN: %s (%s)\n", SSN, isValidSSN());

		return s;
	}

}
