public class MyInteger {

	// Data Members:
	private int num;
	private static int numMyIntegers = 0;

	// Constructors:
	public MyInteger() {
		numMyIntegers++;
	}

	public MyInteger(int num) {
		this.num = num;
		numMyIntegers++;
	}

	// Accessor and Mutator Methods:
	public int getNum() {
		return num;
	}

	public void setNum(int num) {
		this.num = num;
	}

	public static int getNumMyIntegers() {
		return numMyIntegers;
	}

	public static void setNumMyIntegers(int aNumMyIntegers) {
		numMyIntegers = aNumMyIntegers;
	}

	// Other Methods
	public boolean isEven() {
		return num % 2 == 0;
	}

	public boolean isOdd() {
		return num % 2 != 0;
	}

	public boolean isPrime() {
		if (num < 2) {
			return false;
		}

		int topRange = (int) Math.ceil(Math.sqrt(num));

		for (int i = 2; i <= topRange; i++) {
			if (num % i == 0) {
				return false;
			}
		}

		// If we never returned false, then this number is prime; return true.
		return true;
	}

	public boolean isPerfectSquare() {
		int count = 1;

		while (true) {
			if (count * count == num) {
				return true;
			}

			if (count * count > num) {
				break;
			}

			count++;
		}

		// If we never returned, this means it is not a perfect square
		return false;
	}

	@Override
	public String toString() {
		String s = "";

		s += String.format("MyInteger: %d\n", num);
		s += String.format("Even: %s\n", (isEven() ? "yes" : "no"));
		s += String.format("Odd: %s\n", (isOdd() ? "yes" : "no"));
		s += String.format("Prime: %s\n", (isPrime() ? "yes" : "no"));
		s += String.format("Perfect Square: %s\n", (isPerfectSquare() ? "yes" : "no"));

		return s;
	}
}
