public class Student {

	// Data Members
	private int ID;
	private String firstName;
	private String lastName;
	private BirthDate bd;
	private static int numStudents = 0;

	// Constructor
	public Student(int ID, String firstName, String lastName, int birthMonth, int birthDay, int birthYear) {
		this.ID = ID;
		this.firstName = firstName;
		this.lastName = lastName;

		// Make a NEW BirthDate object and send over birthMonth, birthDay, and birthYear as parameters in the constructor
		this.bd = new BirthDate(birthMonth, birthDay, birthYear);

		// Increment the static numStudents
		numStudents++;
	}

	// Accessor & Mutator Methods
	public int getID() {
		return ID;
	}

	public void setID(int ID) {
		this.ID = ID;
	}

	public String getFirstName() {
		return firstName;
	}

	public void setFirstName(String firstName) {
		this.firstName = firstName;
	}

	public String getLastName() {
		return lastName;
	}

	public void setLastName(String lastName) {
		this.lastName = lastName;
	}

	public BirthDate getBd() {
		return bd;
	}

	public void setBd(BirthDate bd) {
		this.bd = bd;
	}

	public static int getNumStudents() {
		return numStudents;
	}

	public static void setNumStudents(int aNumStudents) {
		numStudents = aNumStudents;
	}

	@Override
	public String toString() {
		String s = "";

		s += "Student Information:\n";
		s += String.format("Student ID: %d\n", ID);
		s += String.format("First Name: %s\n", firstName);
		s += String.format("Last Name: %s\n", lastName);
		s += String.format("Birth Date: %s\n", getBd());

		return s;
	}

}
